package tag.dp;

import java.util.HashMap;
import java.util.Map;

public class PalindromicSubstrings {
    static int count = 0;

    public int countSubstrings(String s) {
        count = 0;
        Map<String, Boolean> map = new HashMap<>();

        for (int i = 0; i < s.length(); i++) {
            for (int j = i + 1; j <= s.length(); j++) {
                if (isPalindrom(s.substring(i, j), map)) {
                    count++;
                }
            }
        }

        return count;
    }


    public boolean isPalindrom(String s, Map<String, Boolean> map) {
        if (map.containsKey(s)) {
            return map.get(s);
        }
        if (s.length() <= 1) {
            return true;
        }
        if (s.charAt(0) != s.charAt(s.length() - 1)) {
            return false;
        }
        boolean ret = isPalindrom(s.substring(1, s.length() - 1), map);
        map.put(s, ret);
        return ret;
    }

    public static void main(String[] args) {
        PalindromicSubstrings palindromicSubstrings = new PalindromicSubstrings();
        System.out.println(palindromicSubstrings.countSubstrings("")); //15
    }
}
