package tag.twopointer;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class FindAllAnagramsinaString {
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();

        if (s == null || s.length() == 0 || s.length() < p.length()) {
            return result;
        }

        Map<Character, Integer> pMap = new HashMap<>();
        Map<Character, Integer> sMap = new HashMap<>();

        for (int i = 0; i < p.length(); i++) {
            pMap.put(p.charAt(i), pMap.getOrDefault(p.charAt(i), 0) + 1);
        }
        int pc = pMap.size();
        int sc = 0;

        int i = 0;
        int j = 0;

        while (j < s.length()) {
            if (j < p.length()) {
                char c = s.charAt(j);
                sMap.put(c, sMap.getOrDefault(c, 0) + 1);
                if (pMap.containsKey(c) && sMap.get(c).intValue() == pMap.get(c).intValue()) {
                    sc++;
                }
                j++;
                continue;
            }

            if (sc == pc) {
                result.add(i);
            }

            char c1 = s.charAt(i);
            char c2 = s.charAt(j);
            if (c1 != c2) {
                sMap.put(c1, sMap.get(c1) - 1);
                if (pMap.containsKey(c1) && sMap.get(c1).intValue() < pMap.get(c1).intValue()) {
                    sc--;
                }
                sMap.put(c2, sMap.getOrDefault(c2, 0) + 1);

                if (pMap.containsKey(c2) && sMap.get(c2).intValue() == pMap.get(c2).intValue()) {
                    sc++;
                }
            }

            i++;
            j++;
        }
        if (sc == pc) {
            result.add(i);
        }
        return result;
    }



    public static void main(String[] args) {
//        String s = "";
//        String p = "";
        String s = "aa";
        String p = "bb";
        System.out.println(s.length());
        System.out.println(p.length());

        FindAllAnagramsinaString findAllAnagramsinaString = new FindAllAnagramsinaString();

        System.out.println(Arrays.toString(findAllAnagramsinaString.findAnagrams(s, p).toArray()));
    }
}
